import { Directive, HostListener, Input } from '@angular/core';

@Directive({
  selector: '[appAgeIsNumber]'
})
export class AgeIsNumberDirective {
  @Input() doctorname:string | undefined;
  constructor() { }
  @HostListener('input', ['$event']) onInput(doctorname: any) {
    if(isNaN(parseInt(doctorname.target.value))){
      doctorname.target.value=parseInt(doctorname.target.value);
    }
    else{
      doctorname.target.value='';
    }
  }
}
