import { Component } from '@angular/core';

@Component({
  selector: 'app-admin-left-nav',
  templateUrl: './admin-left-nav.component.html',
  styleUrls: ['./admin-left-nav.component.css']
})
export class AdminLeftNavComponent {
  isMenu:boolean | undefined;
  open_menu(){
    this.isMenu= true;
  }
}
