import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-remove-patient',
  templateUrl: './remove-patient.component.html',
  styleUrls: ['./remove-patient.component.css']
})
export class RemovePatientComponent {
  removePatientForm=new FormGroup({
    patient_id:new FormControl('',[Validators.required])
  })
  get patient_id(){
    return this.removePatientForm.get('patient_id');
  }
  removePatient(){
    console.warn(this.removePatientForm.value);
    alert("Removed Patient Successfully");
  }
}
