import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-book-appointment',
  templateUrl: './book-appointment.component.html',
  styleUrls: ['./book-appointment.component.css']
})
export class BookAppointmentComponent {
  isLoading:boolean | undefined;
  bookAppointmentForm=new FormGroup({
    patient_id:new FormControl('',[Validators.required]),
    doctor_id:new FormControl('',[Validators.required]),
    appointment_date:new FormControl('',[Validators.required]),
    payment: new FormControl('',Validators.required)
  })
  constructor(private router:Router){

  }
  get patient_id(){
    return this.bookAppointmentForm.get('patient_id');
  }
  get doctor_id(){
    return this.bookAppointmentForm.get('doctor_id');
  }
  get appointment_date(){
    return this.bookAppointmentForm.get('appointment_date');
  }
  get payment(){
    return this.bookAppointmentForm.get('payment');
  }

  bookAppointment(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 5000);
    console.warn(this.bookAppointmentForm.value);
    alert("Booked Appointment Successfully");
  }
  showPage(){
    this.isLoading=false;
  }
}
