import { Component, ElementRef, HostListener, Input } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.css']
})
export class ChangePasswordComponent {
  isLoading:boolean | undefined;
  changePasswordForm = new FormGroup({
    patient_id:new FormControl('',[Validators.required,Validators.pattern('^(?=.*[0-9])')]),
    change_old_password:new FormControl('',[Validators.required,Validators.pattern('(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*_]).{8,}')]),
    confirm_new_password:new FormControl('',[Validators.required,Validators.pattern('(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*_]).{8,}')])
  });
  get patient_id(){
    return this.changePasswordForm.get('patient_id');
  }
  get change_old_password(){
    return this.changePasswordForm.get('change_old_password');
  }
  get confirm_new_password(){
    return this.changePasswordForm.get('confirm_new_password');
  }
  constructor(private el:ElementRef) { }
  changePasswordFormSubmit(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 3000);
    if(this.changePasswordForm.get('change_old_password')==this.changePasswordForm.get('confirm_new_password')){
      alert("Changed Succesfully");
    }
    else{
      alert("Cannot Change");
      alert("Both the passwords are not matching")
    }
  }
  showPage(){
    this.isLoading=false;
   /* if(!this.isLoading){
      this.router.navigate(['/admin/admin-header/']);
    }*/
  }
}
