import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-updatenurse',
  templateUrl: './updatenurse.component.html',
  styleUrls: ['./updatenurse.component.css']
})
export class UpdatenurseComponent {
  isLoading:boolean | undefined;
  constructor(){}
  updateNurseForm=new FormGroup({
    nurse_id:new FormControl('',[Validators.required]),
    nurse_name:new FormControl('',[Validators.required,Validators.pattern('^[A-Za-z ]*$')]),
    gender:new FormControl('',[Validators.required]),
    dob:new FormControl('',[Validators.required]),
    address:new FormControl('',[Validators.required]),
    contact_no:new FormControl('',[Validators.required,Validators.pattern('^[0-9]*$'),Validators.minLength(10),Validators.maxLength(10)]),
    email_id:new FormControl('',[Validators.required,Validators.pattern('[a-z0-9]+@[a-z]+.[a-z].{2,3}')]),
    username:new FormControl('',[Validators.required,Validators.pattern('[^ ]*$')]),
    password:new FormControl('',[Validators.required,Validators.pattern("(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*_]).{8,}")])
  });
  get nurse_id(){
    return this.updateNurseForm.get('nurse_id');
  }
  get nurse_name(){
    return this.updateNurseForm.get('nurse_name');
  }
  get Gender(){
    return this.updateNurseForm.get('gender');
  }
  get Dob(){
    return this.updateNurseForm.get('dob');
  }
  get Address(){
    return this.updateNurseForm.get('address');
  }
  get Contact_No(){
    return this.updateNurseForm.get('contact_no');
  }
  get Email_id(){
    return this.updateNurseForm.get('email_id');
  }
  get Username(){
    return this.updateNurseForm.get('username');
  }
  get Password(){
    return this.updateNurseForm.get('password');
  }
  updateNurse(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 5000);
    console.warn(this.updateNurseForm.value);
    alert("Updated Nurse Successfully");
  }
  showPage(){
    this.isLoading=false;
  }
}
