import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-my-profile',
  templateUrl: './my-profile.component.html',
  styleUrls: ['./my-profile.component.css']
})
export class MyProfileComponent {
  isLoading:boolean | undefined;
  myProfileForm=new FormGroup({
    full_name:new FormControl('',[Validators.required,Validators.pattern('^[A-Za-z ]*$')]),
    gender:new FormControl('',[Validators.required]),
    dob:new FormControl('',[Validators.required]),
    address:new FormControl('',[Validators.required]),
    contact_no:new FormControl('',[Validators.required,Validators.minLength(10),Validators.maxLength(10)]),
    email_id:new FormControl('',[Validators.required,Validators.pattern('[a-z0-9]+@[a-z]+\.[a-z].{2,3}')]),
    designation:new FormControl('',[Validators.required,Validators.pattern('^[A-Za-z ]*$')]),
    degree:new FormControl('',[Validators.required,Validators.pattern('^[A-Za-z ]*$')])
  });
  get full_name(){
    return this.myProfileForm.get('full_name');
  }
  get degree(){
    return this.myProfileForm.get('degree');
  }
  get Gender(){
    return this.myProfileForm.get('gender');
  }
  get Dob(){
    return this.myProfileForm.get('dob');
  }
  get Address(){
    return this.myProfileForm.get('address');
  }
  get Contact_No(){
    return this.myProfileForm.get('contact_no');
  }
  get Email_id(){
    return this.myProfileForm.get('email_id');
  }
  get designation(){
    return this.myProfileForm.get('designation');
  }
  myProfileSubmit(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    },5000);
    console.warn(this.myProfileForm.value);
    alert("Added Patient Successfully");
  }
  showPage(){
    this.isLoading=false;
    }
}
