import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-remove-nurse',
  templateUrl: './remove-nurse.component.html',
  styleUrls: ['./remove-nurse.component.css']
})
export class RemoveNurseComponent {
  isLoading:boolean | undefined;
  removeNurseForm=new FormGroup({
    nurse_id:new FormControl('',[Validators.required,Validators.pattern('^[0-9]*$')])
  })
  get nurse_id(){
    return this.removeNurseForm.get('nurse_id');
  }
  removeNurse(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 5000);
    console.warn(this.removeNurseForm.value);
    alert("Removed Nurse Successfully");
  }
  showPage(){
    this.isLoading=false;
  }
}
