import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators} from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css']
})
export class LoginPageComponent {
  isLoading:boolean | undefined;
  loginForm = new FormGroup({
    emailId:new FormControl('',[Validators.required,Validators.email]),
    password:new FormControl('',[Validators.required,
    Validators.pattern("(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}")]),
    userType:new FormControl('',[Validators.required])
    /*userType_doctor:new FormControl('',[Validators.required]),
    userType_patient:new FormControl('',[Validators.required]),*/
  });
  get emailId(){
    return this.loginForm.get('emailId');
  }
  get password(){
    return this.loginForm.get('password');
  }
  get userType(){
    return this.loginForm.get('userType');
  }
  /*get userType(){
    return this.loginForm.get('userType_admin');
  }
  get userType_doctor(){
    return this.loginForm.get('userType_doctor');
  }
  get userType_patient(){
    return this.loginForm.get('userType_doctor');
  }*/
  constructor(private router:Router){

  }
  loginFormSubmit(){
    console.log(this.loginForm.value);
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 2000);
    if(!this.isLoading){
      this.router.navigate(['/admin-header']);
    }
  }
  showPage(){
    this.isLoading=false;
  }
  /*onFormSubmit(loginform:{
    txt_emailId:string,
    txt_password:string,
    userType:string
  }){
    /*this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 2000);
    //var x = document.getElementsByTagName("div");
    //x[0].style.display = "none";
  }*/
  /*showPage(){
    this.isLoading=false;
  }*/
}
