import { Directive, ElementRef, HostListener, Input } from '@angular/core';

@Directive({
  selector: '[appAgeIsNumber]'
})
export class AgeIsNumberDirective {
  @Input() txt_age:string | undefined;
  constructor(private el:ElementRef) { }
  @HostListener('input', ['$event']) onInput(age: any) {
    if(isNaN(parseInt(age.target.value))){
      age.target.value='';
      alert(true);
    }
    else{
      age.target.value=parseInt(age.target.value);
    }
  }
}
