import { Directive, ElementRef, HostListener, Input } from '@angular/core';

@Directive({
  selector: '[appNameNotANumber]'
})
export class NameNotANumberDirective {
  @Input() name:string | undefined;
  constructor() { }
  @HostListener('input', ['$event']) onInput(name: any) {
    if(isNaN(parseInt(name.target.value))){
      name.target.value=parseInt(name.target.value);
    }
    else{
      name.target.value='';
    }
  }
}
