import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.css']
})
export class ChangePasswordComponent {
  isLoading:boolean | undefined;
  changePasswordForm = new FormGroup({
    patient_id:new FormControl('',[Validators.required]),
    change_old_password:new FormControl('',[Validators.required]),
    confirm_new_password:new FormControl('',[Validators.required])
  });
  get patient_id(){
    return this.changePasswordForm.get('patient_id');
  }
  get change_old_password(){
    return this.changePasswordForm.get('change_old_password');
  }
  get confirm_new_password(){
    return this.changePasswordForm.get('confirm_new_password');
  }
  constructor(){

  }
  changePasswordFormSubmit(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 3000);
    alert("Changed Succesfully");
  }
  showPage(){
    this.isLoading=false;
   /* if(!this.isLoading){
      this.router.navigate(['/admin/admin-header/']);
    }*/
  }
  clearForm(){
    /*this.loginForm.emailId = " ";
    this.loginForm.value.password = "";
    this.loginForm.value.userType = "";*/

  }
}
