import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.css']
})
export class ChangePasswordComponent {
  isLoading:boolean | undefined;
  changeUsernameForm = new FormGroup({
    change_display_name:new FormControl('',[Validators.required]),
    change_username:new FormControl('',[Validators.required])
  });
  get change_display_name(){
    return this.changeUsernameForm.get('change_display_name');
  }
  get change_username(){
    return this.changeUsernameForm.get('change_username');
  }

  constructor(){

  }
  changeUsernameFormSubmit(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 3000);
    alert("Changed Succesfully");
  }
  showPage(){
    this.isLoading=false;
   /* if(!this.isLoading){
      this.router.navigate(['/admin/admin-header/']);
    }*/
  }
  clearForm(){
    /*this.loginForm.emailId = " ";
    this.loginForm.value.password = "";
    this.loginForm.value.userType = "";*/

  }
}
