import { Component } from '@angular/core';

@Component({
  selector: 'app-remove-appointment',
  templateUrl: './remove-appointment.component.html',
  styleUrls: ['./remove-appointment.component.css']
})
export class RemoveAppointmentComponent {
  isLoading:boolean | undefined;
  removePatientForm=new FormGroup({
    patient_id:new FormControl('',[Validators.required])
  })
  get patient_id(){
    return this.removePatientForm.get('patient_id');
  }
  removePatient(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    },5000);
    console.warn(this.removePatientForm.value);
    alert("Removed Patient Successfully");
  }
  showPage(){
    this.isLoading=false;
    }
}
