import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-change-username',
  templateUrl: './change-username.component.html',
  styleUrls: ['./change-username.component.css']
})
export class ChangeUsernameComponent {
  isLoading:boolean | undefined;
  changeUsernameForm = new FormGroup({
    change_display_name:new FormControl('',[Validators.required,Validators.pattern('[a-z0-9]+@[a-z]+\.[a-z].{2,3}')]),
    change_username:new FormControl('',[Validators.required,
    Validators.pattern('(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%^&*_]).{8,}')]),
    userType:new FormControl('',[Validators.required])
  });
  get change_display_name(){
    return this.changeUsernameForm.get('change_display_name');
  }
  get change_username(){
    return this.changeUsernameForm.get('change_username');
  }

  constructor(private router:Router){

  }
  changeUsernameFormSubmit(){
    this.isLoading=true;
    setTimeout(() => {
      this.showPage()
    }, 3000);
  }
  showPage(){
    this.isLoading=false;
    if(!this.isLoading){
      this.router.navigate(['/admin/dashboard']);
    }
  }
  clearForm(){
    /*this.loginForm.emailId = " ";
    this.loginForm.value.password = "";
    this.loginForm.value.userType = "";*/

  }
}
