import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-remove-doctor',
  templateUrl: './remove-doctor.component.html',
  styleUrls: ['./remove-doctor.component.css']
})
export class RemoveDoctorComponent {
  removeDoctorForm=new FormGroup({
    doctor_id:new FormControl('',[Validators.required])
  })
  get doctor_id(){
    return this.removeDoctorForm.get('doctor_id');
  }
  removeDoctor(){
    console.warn(this.removeDoctorForm.value);
    alert("Removed Doctor Successfully");
  }
}
